import React, { useState, useEffect, useRef } from "react";
import {
  IonHeader,
  IonPage,
  IonToolbar,
  IonTitle,
  IonContent,
  IonItem,
  IonLabel,
  IonInput,
  IonButton,
  IonText,
  IonMenuButton,
  IonSelect,
  IonSelectOption,
  IonTextarea,
} from "@ionic/react";
import { IUrlOptions } from "../models/rest-api.model";
import { RemoteService } from "../services/remote.service";
import { type } from "os";
import { Method } from "ionicons/dist/types/stencil-public-runtime";
import { setupMaster } from "cluster";
import axios  from 'axios';
import { Geolocation } from '@capacitor/geolocation';
import {usePhotoGallery, UserPhoto} from '../hooks/usePhotoGallery';
import {camera, trash, close} from 'ionicons/icons'


// console.log('Current', coordinates);
const InsertSignilement: React.FC = ({ history }: any) => {
  const remoteService = new RemoteService();
  const [error, setError] = useState({ error: false });
  const loginForm = useRef<HTMLFormElement>(null);
  const [lType,setLType] = useState([]);
  const [lon,setLon]=useState(Number);
  const [lat,setLat]=useState(Number);
  const geolocation  = Geolocation;
  const { photos, takePhoto } = usePhotoGallery();
  const [photoToDelete, setPhotoToDelete] = useState<UserPhoto>();
  const [tabProblem, setTabProblem] = useState([]);
  
  

  // console.log(lon+"-"+lat);
  const sendGetRequest = async () => {
    return axios({
      url: 'http://localhost:3011/types',
      method: 'GET'
    }).then(Response =>{
      return Response.data;
    }).catch((error) => {
      console.log('Error get type', error)
    })
  };
  useEffect(() => {
    sendGetRequest().then(data => setLType(data));
    
  })

  const getLocation = () => {
    geolocation.getCurrentPosition().then((Resp) => {
      setLon(Resp.coords.longitude);
      setLat(Resp.coords.latitude);
      console.log(`${Resp.coords.latitude},${Resp.coords.longitude}`);
    }).catch((error) => {
      console.log('No connection default location',error)
      setLon(-18.9860485);
      setLat(47.5324514);
    });
    console.log(`${lat},${lon}`);
  }


  const Valider = () => {
    // var formData = new FormData()
    // formData.append('mail', email)
    // formData.append('mdp', password)
    // var url = 'http://localhost:3011/login/utilisateurs'
    // console.log(email)
    // fetch(url, {
    //     method: 'POST',
    //     body: formData,
    // })
    //     .then((res) => res.json())
    //     .then((result) => {
    //         window.location.href = '/InsertSignilement'
    //         console.log("toto =",result['token'])
    //         sessionStorage.setItem("Token",result['token'])
    //         localStorage.setItem('Token', result['token'])

    //     })
    //     .catch((error) => {
    //       console.log(error);
    //     });

    getLocation();

  }

  

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonMenuButton slot="start"></IonMenuButton>
          <IonTitle>Nouveau signilement</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        
          <IonItem>
            <IonLabel position="floating">Object</IonLabel>
            <IonInput
              placeholder=""
              type="text"
              name="Object"
              id="Object"
            ></IonInput>
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Type</IonLabel>
            <IonInput type="text" name="type" id="type"></IonInput>

            <IonSelect placeholder="Choisissez le type d'incidents" name="type" id="type"  >
              {
                lType.map((el, index) => {
                  return (<IonSelectOption value={el['id']} key={index} >{el['nom']}</IonSelectOption>);
                })
              }
            </IonSelect>

          </IonItem>
         
          <IonItem>
            <IonLabel position="floating">description</IonLabel>
            <IonTextarea
              name="description"
              id="description"
            ></IonTextarea>
          </IonItem>
          
          <div className="button-wrapper">
          <IonButton expand="block" 
          onClick={() => Valider()}
          >
                            Login
          </IonButton>
          </div>
      </IonContent>
    </IonPage>
  );
};

// export default Details;
export default InsertSignilement;
