import React, { useState, useEffect, useRef } from "react";
import {
  IonHeader,
  IonPage,
  IonToolbar,
  IonTitle,
  IonContent,
  IonItem,
  IonLabel,
  IonInput,
  IonButton,
  IonText,
  IonMenuButton,
  IonSelect,
  IonSelectOption,
  IonTextarea,
  IonGrid,
  IonRow,
  IonCol,
  IonImg,
  IonActionSheet,
  IonFab,
  IonFabButton,
  IonIcon,
} from "@ionic/react";
import { IUrlOptions } from "../models/rest-api.model";
import { RemoteService } from "../services/remote.service";
import { type } from "os";
import { Method } from "ionicons/dist/types/stencil-public-runtime";
import { setupMaster } from "cluster";
import axios  from 'axios';
import { Geolocation } from '@capacitor/geolocation';
import {usePhotoGallery, UserPhoto} from '../hooks/usePhotoGallery';
import {camera, trash, close} from 'ionicons/icons'
import { Session } from "inspector";


// console.log('Current', coordinates);
const InsertSignilement: React.FC = ({ history }: any) => {
  const remoteService = new RemoteService();
  const [error, setError] = useState({ error: false });
  const loginForm = useRef<HTMLFormElement>(null);
  const [lType,setLType] = useState([]);
  const [lon,setLon]=useState(Number);
  const [lat,setLat]=useState(Number);
  const geolocation  = Geolocation;
  const { photos, takePhoto } = usePhotoGallery();
  const { deletePhoto } = usePhotoGallery();
  const [photoToDelete, setPhotoToDelete] = useState<UserPhoto>();
  const [tabProblem, setTabProblem] = useState([]);
  let id="";
  let region= useState(null);
  let utilisateur = sessionStorage.getItem("id");
  const [objet,setObjet] = useState("");
  const current = new Date();
  const datesignalement = useState(`${current.getFullYear}-${current.getMonth}-${current.getDate}`);
  const protocole = useState("PRO1");
  const description = useState("PRO1");
  const longitude = useState(lon);
  const latitude = useState(lat);
  
  

  // console.log(lon+"-"+lat);
  const sendGetRequest = async () => {
    return axios({
      url: 'http://localhost:3011/types',
      method: 'GET'
    }).then(Response =>{
      return Response.data;
    }).catch((error) => {
      console.log('Error get type', error)
    })
  };
  useEffect(() => {
    sendGetRequest().then(data => setLType(data));
    
  })

  const getLocation = () => {
    geolocation.getCurrentPosition().then((Resp) => {
      setLon(Resp.coords.longitude);
      setLat(Resp.coords.latitude);
      console.log(`${Resp.coords.latitude},${Resp.coords.longitude}`);
    }).catch((error) => {
      console.log('No connection default location',error)
      setLon(-18.9860485);
      setLat(47.5324514);
    });
    console.log(`${lat},${lon}`);
  }


  const Valider = async() => {
    getLocation();
    id ="SIG" + (Math.floor(Math.random() * (5000 - 101 + 1)) + 101).toString();
    axios
      .post("http://localhost:3011/signalements/objet", {
        id,
        type,
        region,
        utilisateur,
        objet,
        datesignalement,
        protocole,
        description,
        longitude,
        latitude
      })
      .then((res) => {
        // console.log("UT"+(Math.floor(Math.random()*(11 -0 +1))+10).toString())
        // setSuccess("inscription terminée");
        //window.location.href = "/login";
      })
      .catch((error) => console.log(error));

  }
  const envoyer =async () => {
  //   var cp=[];
  //   for(var i=0;i<photos.length;i++){
  //     var b=await (await fetch(photos[i].webviewPath!)).blob();
  //     cp.push(b);
  //   }
  //   var formData = new FormData()
  //   for(var i=0;i<cp.length;i++){
  //   formData.append('files',cp[i],"photo.jpeg");
  //  }
  //   formData.append('problem', prob)
  //   formData.append('description', descri)
  //   formData.append('coordonneX', "" + lat)
  //   formData.append('coordonneY', "" + long)

  //   var url = "http://localhost:6565/api/ajout/signalement/photos/"+localStorage.getItem('token')
  //   fetch(url, {
  //     method: 'POST',
  //     body: formData,
  //   })
  //     .then((res) => res.json())
  //     .then((result) => {
  //       setMessage("Envoye du signalement réussie");
  //     })
  //     .catch((error) => {
  //       setErreur("Echec de l'envoye");
  //     });
  }
  

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonMenuButton slot="start"></IonMenuButton>
          <IonTitle>Nouveau signilement</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        
          <IonItem>
            <IonLabel position="floating">Object</IonLabel>
            <IonInput
              placeholder=""
              type="text"
              name="Object"
              id="Object"
            ></IonInput>
          </IonItem>

          <IonGrid>
            <IonRow>
              {photos.map((photo,index) => (
                <IonCol size="6" key={index}>
                  <IonImg   onClick={() => setPhotoToDelete(photo)} src={photo.webviewPath} />
                </IonCol>
              ))}
              
            </IonRow>
          </IonGrid>
          <IonFab vertical="bottom" horizontal="center" slot="fixed">
          <IonFabButton onClick={() => takePhoto()}>
            <IonIcon icon={camera}></IonIcon>
          </IonFabButton>
        </IonFab>
        <IonActionSheet
          isOpen={!!photoToDelete}
          buttons={[{
            text: 'Delete',
            role: 'destructive',
            icon: trash,
            handler: () => {
              if (photoToDelete) {
                deletePhoto(photoToDelete);
                setPhotoToDelete(undefined);
              }
            }
          }, {
            text: 'Cancel',
            icon: close,
            role: 'cancel'
          }]}
          onDidDismiss={() => setPhotoToDelete(undefined)}
        />

          <IonItem>
            <IonLabel position="floating">Type</IonLabel>

            <IonSelect placeholder="Choisissez le type d'incidents" name="type" id="type"  >
              {
                lType.map((el, index) => {
                  return (<IonSelectOption value={el['id']} key={index} >{el['nom']}</IonSelectOption>);
                })
              }
            </IonSelect>

          </IonItem>
         
          <IonItem>
            <IonLabel position="floating">description</IonLabel>
            <IonTextarea
              name="description"
              id="description"
            ></IonTextarea>
          </IonItem>
          
          <div className="button-wrapper">
          <IonButton expand="block" 
            onClick={() => Valider()}
          >
            Valider
          </IonButton>
          </div>
      </IonContent>
    </IonPage>
  );
};

// export default Details;
export default InsertSignilement;
