import React, { useState, useEffect, useRef } from "react";
import {
  IonHeader,
  IonPage,
  IonToolbar,
  IonTitle,
  IonContent,
  IonItem,
  IonLabel,
  IonInput,
  IonButton,
  IonRow,
  IonCol,
  IonIcon,
  IonText,
} from "@ionic/react";
import { IUrlOptions } from "../models/rest-api.model";
import { RemoteService } from "../services/remote.service";
import { useHistory } from "react-router-dom";
import { personCircle } from "ionicons/icons";

const LoginPage: React.FC = ({ history }: any) => {
  const [email, setEmail] = useState("");
  const [password, setPwd] = useState("");
  const [erreur, setErreur] = useState("");
  const remoteService = new RemoteService();
  const [error, setError] = useState({ error: false });
  const loginForm = useRef<HTMLFormElement>(null);

  const verifAuthentif = () => {
    var formData = new FormData();
    formData.append("email", email);
    formData.append("mdp", password);
    var url = "http://localhost:3011/login/utilisateurs";
    console.log(email);
    fetch(url, {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((result) => {
        window.location.href = "/accueil";
        localStorage.setItem("token", result["refToken"]);
      })
      .catch((error) => {
        setErreur("Echec Auth ! Veuillez réessayer");
      });
  };

  // useEffect(() => {
  //     let token = sessionStorage.getItem('userToken');
  //     console.log('loginForm :', loginForm);
  //     if (loginForm && loginForm.current) {
  //         loginForm.current.reset();
  //     }
  //     if (token) {
  //         history.push('/home');
  //     } else {
  //         history.push('/login');
  //     }
  // }, []);

  // if(!sessionStorage.getItem("Token")){
  //     history.push('../App');
  // }

  const handleSubmit = (event: any) => {
    event.preventDefault();

    const [email, setEmail] = useState("");
    const [password, setPwd] = useState("");
    const [erreur, setErreur] = useState("");

    const verifAuthentif = () => {
      var formData = new FormData();
      formData.append("email", email);
      formData.append("mdp", password);
      var url = "http://localhost:6565/api/user/login";
      console.log(email);
      fetch(url, {
        method: "POST",
        body: formData,
      })
        .then((res) => res.json())
        .then((result) => {
          window.location.href = "/accueil";
          localStorage.setItem("token", result["refToken"]);
        })
        .catch((error) => {
          setErreur("Echec Auth ! Veuillez réessayer");
        });
    };

    // Syntax : var formData = new FormData(form)
    // Ref : https://medium.com/@everdimension/how-to-handle-forms-with-just-react-ac066c48bd4f
    // const form = event.target.closest("form");
    // const formData = new FormData(form);

    // const formDataObj = {
    //   username: formData.get("email"),
    //   password: formData.get("mdp"),
    // };

    // const options: IUrlOptions = {
    //   endPoint: `login`,
    //   restOfUrl: "",
    //   isSecure: true,
    //   contentType: "application/json",
    // };

    // remoteService.request("POST", options, formDataObj).then((data) => {
    //   if (formDataObj.password == "test" && formDataObj.username == "test") {
    //     // data.username ="aa";
    //     // console.log("yes");

    //     sessionStorage.setItem("Token", "aa");

    //     history.push("/InsertSignilement");
    //   } else {
    //     alert("mots de passe incorrect");
    //   }
    // });
    // const options: IUrlOptions = {
    //     endPoint: ``,
    //     restOfUrl: '',
    //     isSecure: true,
    //     contentType: 'application/json'
    // };

    // remoteService.request('GET', options).then((data) => {
    //     if (formDataObj.password =="aa") {
    //                 // data.username ="aa";
    //                 // console.log("yes");

    //                 sessionStorage.setItem('Token', "aa");

    //                 history.push('/home');
    //     }
    //             else{
    //                 alert("mots de passe incorrect");
    //             }
    // })
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Connectez vous pour signaler</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        <IonRow>
          <IonCol>
            <IonIcon
              style={{ fontSize: "70px", color: "#0040ff" }}
              icon={personCircle}
            />
          </IonCol>
        </IonRow>
        <br></br>
        {erreur && (
          <IonText color="danger">
            <i>{erreur}</i>
          </IonText>
        )}

        <IonItem>
          <IonLabel position="floating">E-mail</IonLabel>
          <IonInput
            required
            type="text"
            value={email}
            onIonChange={(e) => setEmail(e.detail.value!)}
          ></IonInput>
        </IonItem>
        <IonItem>
          <IonLabel position="floating">mot de passe</IonLabel>
          <IonInput
            required
            type="password"
            value={password}
            onIonChange={(e) => setPwd(e.detail.value!)}
          ></IonInput>
        </IonItem>
        <div className="button-wrapper">
          <IonButton expand="block" onClick={() => verifAuthentif()}>
            Login
          </IonButton>
        </div>
        <p style={{ fontSize: "medium" }}>
          pas encore de compte? <a href="/inscription">S'inscrire'!</a>
        </p>
      </IonContent>
    </IonPage>
  );
};

// export default Details;
export default LoginPage;
