import React, { useState, useEffect, useRef } from "react";
import {
  IonHeader,
  IonPage,
  IonToolbar,
  IonTitle,
  IonContent,
  IonItem,
  IonLabel,
  IonInput,
  IonButton,
  IonText,
  IonMenuButton,
  IonSelect,
  IonSelectOption,
  IonTextarea,
} from "@ionic/react";
import { IUrlOptions } from "../models/rest-api.model";
import { RemoteService } from "../services/remote.service";
import { type } from "os";
import { Method } from "ionicons/dist/types/stencil-public-runtime";
import { setupMaster } from "cluster";
import axios  from 'axios';
import { Geolocation } from '@capacitor/geolocation';


// console.log('Current', coordinates);
const InsertSignilement: React.FC = ({ history }: any) => {
  const remoteService = new RemoteService();
  const [error, setError] = useState({ error: false });
  const loginForm = useRef<HTMLFormElement>(null);
  const [lType,setLType] = useState([]);
  const [lon,setLon]=useState(Number);
  const [lat,setLat]=useState(Number);
  const geolocation  = Geolocation;
  
  

  // console.log(lon+"-"+lat);
  const sendGetRequest = async () => {
    const response = await axios({
      url: 'http://localhost:3011/types',
      method: 'GET'
    }).then((response) => {
      return response.data;
    }
    ).catch((error) => {
      console.log('Error getting location',error)
    });
    
  };
  useEffect(() => {
    sendGetRequest().then(data => setLType(data));
    // geolocation.getCurrentPosition().then((Resp) => {
    //   setLon(Resp.coords.longitude);
    //   setLat(Resp.coords.latitude);
    // }).catch((error) => {
    //   console.log('Error getting location',error)
    // });
  })

  const handleSubmit = (event: any) => {
    event.preventDefault();

  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonMenuButton slot="start"></IonMenuButton>
          <IonTitle>Nouveau signilement</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        <form
          ref={loginForm}
          name="formLogin"
          id="formLogin"
          method="POST"
          onSubmit={handleSubmit}
          encType="multipart/form-data"
        >
          <IonItem>
            <IonLabel position="floating">Object</IonLabel>
            <IonInput
              placeholder=""
              type="text"
              name="Object"
              id="Object"
            ></IonInput>
          </IonItem>
          <IonItem>
            <IonLabel position="floating">Type</IonLabel>
            <IonInput type="text" name="type" id="type"></IonInput>

            <IonSelect placeholder="Choisissez le type d'incidents" name="type" id="type"  >
              {
                lType.map((el, index) => {
                  return (<IonSelectOption value={el['id']} key={index} >{el['nom']}</IonSelectOption>);
                })
              }
            </IonSelect>

          </IonItem>
         
          {/* <IonItem>
                        <IonLabel position="floating" >Utilisateur</IonLabel>
                        <IonInput type="text" name="Utilisateur" id="datedenaissance" ></IonInput>
                    </IonItem> */}
          
          <IonItem>
            <IonLabel position="floating">description</IonLabel>
            <IonTextarea
              name="description"
              id="description"
            ></IonTextarea>
          </IonItem>
          
          <div className="button-wrapper">
            <IonButton type="submit">valider</IonButton>
          </div>
        </form>
      </IonContent>
    </IonPage>
  );
};

// export default Details;
export default InsertSignilement;
