import React, { useState, useEffect, useRef } from "react";
import {
  IonHeader,
  IonPage,
  IonToolbar,
  IonTitle,
  IonContent,
  IonItem,
  IonLabel,
  IonInput,
  IonButton,
  IonText,
} from "@ionic/react";
import { IUrlOptions } from "../models/rest-api.model";
import { RemoteService } from "../services/remote.service";

const InsertSignilement: React.FC = ({ history }: any) => {
  const remoteService = new RemoteService();
  const [error, setError] = useState({ error: false });
  const loginForm = useRef<HTMLFormElement>(null);

  // useEffect(() => {
  //     let token = sessionStorage.getItem('userToken');
  //     console.log('loginForm :', loginForm);
  //     if (loginForm && loginForm.current) {
  //         loginForm.current.reset();
  //     }
  //     if (token) {
  //         history.push('/home');
  //     } else {
  //         history.push('/login');
  //     }
  // }, []);

  const handleSubmit = (event: any) => {
    event.preventDefault();

    // Syntax : var formData = new FormData(form)
    // Ref : https://medium.com/@everdimension/how-to-handle-forms-with-just-react-ac066c48bd4f
    // const form = event.target.closest('form');
    // const formData = new FormData(form);

    // const formDataObj = {
    //     nom: formData.get('nom'),
    //     prenom: formData.get('prenom'),
    //     dateNaissance: formData.get('datedenaissance'),
    //     mail: formData.get('mail'),
    //     mpd: formData.get('mdp')
    // };

    // const options: IUrlOptions = {
    //     endPoint: `login`,
    //     restOfUrl: '',
    //     isSecure: true,
    //     contentType: 'application/json'
    // };

    // remoteService.request('POST', options, formDataObj).then((data) => {
    //     if (formData.get('email')=="aa") {
    //         // data.username ="aa";
    //         console.log("yes");
    //         //sessionStorage.setItem('userToken', data.token);
    //         history.push('/home');
    //     }
    //     else{
    //         alert("vous avez pas de compte");
    //     }
    // });
  };

  return (
    <IonPage>
      <IonHeader>
        <IonToolbar>
          <IonTitle>Nouveau signilement</IonTitle>
        </IonToolbar>
      </IonHeader>

      <IonContent>
        <form
          ref={loginForm}
          name="formLogin"
          id="formLogin"
          method="POST"
          onSubmit={handleSubmit}
          encType="multipart/form-data"
        >
          <IonItem>
            <IonLabel position="floating">Type</IonLabel>
            <IonInput type="text" name="type" id="type"></IonInput>
          </IonItem>
          <IonItem>
            <IonLabel position="floating">region</IonLabel>
            <IonInput type="text" name="region" id="region"></IonInput>
          </IonItem>
          {/* <IonItem>
                        <IonLabel position="floating" >Utilisateur</IonLabel>
                        <IonInput type="text" name="Utilisateur" id="datedenaissance" ></IonInput>
                    </IonItem> */}
          <IonItem>
            <IonLabel position="floating">Object</IonLabel>
            <IonInput
              placeholder=""
              type="text"
              name="Object"
              id="Object"
            ></IonInput>
          </IonItem>
          <IonItem>
            <IonLabel position="floating">description</IonLabel>
            <IonInput
              type="password"
              name="description"
              id="description"
            ></IonInput>
          </IonItem>
          <IonItem>
            <IonLabel position="floating">longitude</IonLabel>
            <IonInput
              type="password"
              name="longitude"
              id="longitude"
            ></IonInput>
          </IonItem>
          <IonItem>
            <IonLabel position="floating">latitude</IonLabel>
            <IonInput type="password" name="latitude" id="latitude"></IonInput>
          </IonItem>
          <div className="button-wrapper">
            <IonButton type="submit">valider</IonButton>
          </div>
        </form>
      </IonContent>
    </IonPage>
  );
};

// export default Details;
export default InsertSignilement;
